class Allergens {

	/**
	 * Returns the names of the products
	 *
	 * @return allergens_name
	 */
	public function show_allergens_name() {
		global $all_allergens;
		$allergens_name = array();

		foreach ($all_allergens as $allergen) {
			$allergens_name[ $allergen['key'] ] = $allergen['label'];
		}

		return $allergens_name;
	}

	/**
	 * Returns the svg of the products
	 *
	 * @return allergens_svg
	 */
	public function show_allergens_svg()
	{
		global $all_allergens;
		$allergens_svg = array();

		foreach ( $all_allergens as $key => $item ) {
			array_push($allergens_svg, $item['svg']);
		}

		return $allergens_svg;
	}

	/**
	 * Return the svg of the product by name
	 *
	 * @return allergen_svg
	 */
	public function show_allergen_svg($name)
	{
		global $all_allergens;
		$allergen_svg = '';

		foreach ( $all_allergens as $key => $value ) {
			if( $value['key'] == $name )
			{
				$allergen_svg = $value['svg'];
			}
		}

		return $allergen_svg;
	}

	/**
	 * Return the svg of the special allergen by key
	 *
	 * @return allergen_svg
	 */
	public function show_special_allergen_svg($name)
	{
		global $special_allergens;
		$allergen_svg = '';

		foreach ( $special_allergens as $value ) {
			if( $value['key'] == $name )
			{
				$allergen_svg = $value['svg'];
			}
		}

		return $allergen_svg;
	}

	/**
	 * Returns the names of the special allergens
	 *
	 * @return allergens_special_name
	 */
	public function show_special_allergens_name() {
		global $special_allergens;
		$allergens_special_name = array();

		foreach ($special_allergens as $allergen) {
			array_push( $allergens_special_name, $allergen['key'] );
		}

		return $allergens_special_name;
	}

}
